import React from 'react';
import styled from 'styled-components';
import { useMediaQuery } from 'react-responsive';

const StyledMain = styled.div`
    background-color: #FFFFFF;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;

    font-size: calc(10px + 2vmin);
    color: black;
`;

const StyledNavbar = styled.button`
    float: left;
    margin-left: 2.5%;
    border: none;
    background-color: transparent;
`;

const StyledLogo = styled.div`
    text-align: center;
    background-color: #FFFFFF; 
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-size: calc(10px + 2vmin);
    color: black;
    margin-top: 5%;
    margin-bottom: 20%;

    display: block;
    width: 100%;
    text-align: center;
`;

const SytledImage = styled.div`
    margin-right: 2vw;
    img {
        max-width: 100%;
        height: auto;
    }
`;

const StyledText = styled.div`
    text-align: center;
    font-size: ${props => (props.smallScreen ? '1rem' : '1.25rem')};
    margin-bottom: 200px;
    margin-left: 20%;
    margin-right: 20%;
`;

const About_mobile = () => {
    const isSmallScreen = useMediaQuery({ maxWidth: 768 });

    function goMain(e) {
        window.location.href = "/";
    }

    return (
        <StyledMain>
            <StyledLogo className="MainLogo" id="mainLogo">
                <StyledNavbar>
                    <img onClick={showMenu} src="./media/menu_button.png" alt="메뉴버튼" />
                </StyledNavbar>
                <SytledImage>
                    <img onClick={goMain} src="./media/MainLogo/wnrf_green_black.png" alt="메인 로고입니다. 클릭하면 홈으로 돌아갑니다." />
                </SytledImage>
            </StyledLogo>

            <div className="intro" style={{ width: '100%', height: 'auto', overflow: 'hidden' }}>
                <img src="./media/about/about_img.png" alt="about" style={{ maxWidth: '100%' }} />
                <StyledText smallScreen={isSmallScreen}>
                    <h1>Korean Styled Modern Bonsai</h1>
                    {/* ... */}
                </StyledText>
                <StyledText smallScreen={isSmallScreen}>
                    {/* ... */}
                </StyledText>
            </div>

            {/* 나머지 내용 */}
        </StyledMain>
    );
};

export default About_mobile;
